changeSet.sql:
CREATE TABLE BOOK(
    ID SERIAL PRIMARY KEY NOT NULL,
    BOOK_NAME CHARACTER VARYING(128) NOT NULL,
    STOCK_AMOUNT BIGINT NOT NULL,
    COST DOUBLE PRECISION NOT NULL,
    AUTHOR_ID BIGINT NOT NULL,
    UID CHARACTER VARYING(38) NOT NULL,
    CONSTRAINT FK_BOOK_AUTHOR_ID FOREIGN KEY (AUTHOR_ID) REFERENCES AUTHOR(ID)
);
CREATE UNIQUE INDEX ui_book_uid ON BOOK(UID);
